import { decorate, observable, action } from "mobx";
import Api from "../api/index";
import Card from "../models/Card";

//import { decorate, observable, action } from "mobx";

class WriteStore {
  activeStep = 0;
  theme = "";
  message = "";
  uniqueId = "";

  inputMethod = "templates";

  cardFlipped = true;

  constructor(rootStore) {
    this.rootStore = rootStore;
    this.api = new Api(`cards`);
    this.socket = rootStore.uiStore.socket;
  }

  setActiveStep = amount => {
    switch (amount) {
      case "increase":
        return (this.activeStep = this.activeStep + 1);
      case "decrease":
        return (this.activeStep = this.activeStep - 1);
      case "reset":
        return (this.activeStep = 0);
      default:
        return "Unknown stepIndex";
    }
  };

  getStepTitle = stepIndex => {
    switch (stepIndex) {
      case 0:
        return "Welk thema spreekt je het meest aan tijdens het ontdekken van een nieuwe regio, cultuur of land?";
      case 1:
        return "Wat zou je graag vertellen of vragen?";
      case 2:
        return "";
      case 3:
        return "Klaar! Uw kaartje wordt verstuurd";
      default:
        return "Unknown stepIndex";
    }
  };

  handleChangeInput = selectedInput => {
    this.inputMethod = selectedInput;
  };

  handleFlipCard = () => {
    if (!this.cardFlipped) {
      this.cardFlipped = true;
    } else {
      this.cardFlipped = false;
    }
  };

  sendCard = async () => {
    console.log(this.message);

    const card = new Card(
      this.theme,
      this.message,
      this.rootStore.uiStore.selectedLocation,
      [],
      [],
      this.uniqueId
    );
    await this.rootStore.openStore.cards.push(card);
    this.api.createCard(card);

    this.socket.emit(`sendCard`, card);

    this.emptyValues();
  };

  emptyValues = () => {
    this.activeStep = 0;
    this.theme = "";
    this.message = "";
    this.uniqueId = "";
    this.cardFlipped = true;
  };

  getImg = theme => {
    let img;
    console.log(theme.en, this.theme);

    if (this.rootStore.uiStore.userLanguage === "nl") {
      this.theme !== "" && theme.en === this.theme
        ? (img = theme.nl[1])
        : (img = theme.nl[0]);
    } else {
      this.theme !== "" && theme.en === this.theme
        ? (img = theme.fr[1])
        : (img = theme.fr[0]);
    }

    console.log(img);

    return img;
  };
}

decorate(WriteStore, {
  activeStep: observable,
  theme: observable,
  message: observable,
  inputMethod: observable,
  cardFlipped: observable,
  setActiveStep: action,
  handleChangeInput: action,
  handleFlipCard: action,
  sendCard: action,
  emptyValues: action,
  getImg: action
});

export default WriteStore;
