import React from "react";
import { inject, observer, PropTypes } from "mobx-react";
import { Link } from "react-router-dom";

import styled from "styled-components";
import Button from "@material-ui/core/Button";
import { ROUTES } from "../constants";
import backgroundImage from "../assets/img/adminBg.jpg";

const Admin = ({ uiStore }) => {
  return (
    <StyledPage>
      <div>
        <StyledTitle>
          Kies uw taal <br />{" "}
          <StyledTitleSpan>choisissez votre langue</StyledTitleSpan>
        </StyledTitle>
        <div>
          <Button
            variant={uiStore.adminLanguage === "nl" ? "contained" : "text"}
            onClick={() => uiStore.setAdminLanguage("nl")}
          >
            NL
          </Button>
          <p>
            Deze taal stelt u in om de installatie op te starten en heeft geen
            verdere invloed op de installatie zelf.
          </p>
        </div>
        <div>
          <Button
            variant={uiStore.adminLanguage === "fr" ? "contained" : "text"}
            onClick={() => uiStore.setAdminLanguage("fr")}
          >
            FR
          </Button>
          <p>
            Vous définissez cette langue pour l'installation pour démarrer et
            n'a pas plus influence sur l'installation elle-même.
          </p>
        </div>

        <br />

        {uiStore.adminLanguage !== "" ? (
          <Link to={ROUTES.setup}>
            <Button variant="contained">
              {uiStore.adminLanguage === "nl"
                ? content.nl.startBtn
                : content.fr.startBtn}
            </Button>
          </Link>
        ) : null}
      </div>
    </StyledPage>
  );
};

const content = {
  nl: {
    startBtn: "Installatie verder configureren"
  },
  fr: {
    startBtn: "Configurer davantage l'installation"
  }
};

const StyledPage = styled.section`
  width: 100vw;
  height: 100vh;
  background-image: url(${backgroundImage});
  background-size: contain;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const StyledTitle = styled.h1`
  font-size: 40px;
  font-weight: bold;
  color: #8089ce;
  text-align: center;
`;

const StyledTitleSpan = styled.span`
  color: #4e5587;
`;

Admin.propTypes = {
  uiStore: PropTypes.observableObject.isRequired
};

export default inject(`uiStore`)(observer(Admin));
