import React, { useState } from "react";
import { inject, observer, PropTypes } from "mobx-react";
import { withRouter, Link } from "react-router-dom";
import { ROUTES } from "../constants";
import shortid from "shortid";
import QRCode from "qrcode.react";

import styled from "styled-components";
import backgroundImage from "../assets/img/choiceBg.png";

import steps3 from "../assets/img/steps/steps_3.png";

import purpleBtn from "../assets/img/purpleBtn_s.png";
import textBubble from "../assets/img/textBubble_purple.png";
import nextArrow from "../assets/img/nextArrow.png";

const SealCard = ({ openStore, uiStore, writeStore }) => {
  const [count, setCount] = useState(0);

  const handleClickWrite = () => {
    uiStore.selectedAction = "open";
    writeStore.sendCard();
  };

  const getUniqueKey = () => {
    return shortid.generate();
  };

  writeStore.uniqueId = getUniqueKey();

  return (
    <StyledPage>
      <Steps img={steps3}>
        <p>Seal card</p>
      </Steps>
      <div>
        <ControlTitle>Verzegel je envelop!</ControlTitle>
        <ControlText>
          Dit is de laatste controle en hierna kan je je kaartje niet meer
          wijzigen!
        </ControlText>
      </div>
      <img src="" alt="" />
    </StyledPage>
  );
};

const StyledPage = styled.section`
  width: 100vw;
  height: 100vh;
  background-image: url(${backgroundImage});
  background-size: cover;
  z-index: 0;

  & div {
    position: absolute;
    left: 25rem;
    top: 45%;
  }
`;

const Steps = styled.div`
  background-image: url(${props => props.img});
  background-size: contain;
  background-repeat: no-repeat;
  width: 55rem;
  height: 13rem;

  position: fixed;
  top: 5rem;
  left: 5rem;

  & p {
    display: none;
  }
`;

const ControlTitle = styled.h2`
  color: #8089ce;
  font-family: "Nunito";
  font-size: 4rem;
  font-weight: 800;
  line-height: 5rem;
  margin-bottom: 3rem;
`;

const ControlText = styled.p`
  color: #8089ce;
  font-family: "Nunito";
  font-size: 2rem;
  line-height: 3rem;
  font-weight: 600;
  max-width: 25rem;
`;

SealCard.propTypes = {
  openStore: PropTypes.observableObject.isRequired
};

export default inject(
  `openStore`,
  `uiStore`,
  `writeStore`
)(withRouter(observer(SealCard)));
