import React from "react";
import { inject, observer, PropTypes } from "mobx-react";
import Card from "../components/Card";

const Answer = ({ openStore }) => {
  const card = openStore.showCard();

  return (
    <>
      <h1>Dit is jou gekozen kaartje binnen het thema "{card.theme}"!</h1>
      <Card
        isFlipped={true}
        theme={card.theme}
        message={card.message}
        store={openStore}
      />
    </>
  );
};

Answer.propTypes = {
  openStore: PropTypes.observableObject.isRequired
};

export default inject(`openStore`)(observer(Answer));
