import React, { useState } from "react";
import { inject, observer, PropTypes } from "mobx-react";
import { withRouter, Link } from "react-router-dom";
import { ROUTES } from "../constants";
import shortid from "shortid";
import QRCode from "qrcode.react";

import SealCard from "../components/SealCard";

import styled from "styled-components";
import backgroundImage from "../assets/img/adminBg.jpg";
import purpleBtn from "../assets/img/purpleBtn_s.png";
import textBubble from "../assets/img/textBubble_purple.png";
import nextArrow from "../assets/img/nextArrow.png";

const Sent = ({ openStore, uiStore, writeStore }) => {
  const [count, setCount] = useState(0);

  const handleClickWrite = () => {
    uiStore.selectedAction = "open";
    writeStore.sendCard();
  };

  const getUniqueKey = () => {
    return shortid.generate();
  };

  writeStore.uniqueId = getUniqueKey();

  return (
    <StyledPage>

    </StyledPage>
  );
};

const StyledPage = styled.section`
  width: 100vw;
  height: 100vh;
  background-image: url(${backgroundImage});
  background-size: cover;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  z-index: 0;
`;

const ThankLine = styled.p`
  color: #8089ce;
  font-size: 2.5rem;
  font-family: "Nunito";
  font-weight: 600;
`;

const Follow = styled.h2`
  color: #8089ce;
  font-size: 3.5rem;
  font-family: "Nunito";
  font-weight: bold;
  padding: 3.5rem 0 2rem 0;

  & span {
    color: #4e5587;
  }
`;

const Others = styled.p`
  color: #8089ce;
  font-size: 2.5rem;
  font-family: "Nunito";
  font-weight: 600;
  padding-bottom: 4rem;
`;

const Codes = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  padding: 5rem;
`;

const UniqueId = styled.p`
  color: #4e5587;
  font-size: 2.5rem;
  font-family: "Nunito";
  font-weight: bold;
  padding-top: 2rem;
`;

const MoreBtn = styled.div`
  position: absolute;
  right: 5rem;
  bottom: 5rem;
`;

const MoreTxt = styled.div`
  background-image: url(${textBubble});
  background-size: contain;
  width: 26rem;
  height: 20rem;
  position: relative;
  top: 2rem;
  left: -8rem;
  transform: rotate(2deg);

  & p {
    color: white;
    font-size: 2rem;
    line-height: 2.5rem;
    font-family: "Nunito";
    font-weight: bold;
    transform: rotate(-6deg);
    max-width: 20rem;
    text-align: center;
    position: relative;
    top: 4rem;
    left: 3rem;
  }
`;

const WriteBtn = styled.button`
  border: none;
  background-image: url(${purpleBtn});
  background-size: contain;
  background-color: transparent;
  background-repeat: no-repeat;
  width: 40rem;
  height: 7.4rem;

  color: white;
  font-size: 2rem;
  font-family: "Nunito";
  font-weight: 600;

  display: flex;
  justify-content: center;
  align-items: center;

  & span {
    margin-right: 1rem;
  }

  &:focus {
    outline: none;
    transform: scale(0.95);
  }
`;

Sent.propTypes = {
  openStore: PropTypes.observableObject.isRequired
};

export default inject(
  `openStore`,
  `uiStore`,
  `writeStore`
)(withRouter(observer(Sent)));
