import React from "react";
import { inject, observer, PropTypes } from "mobx-react";

const Answer = ({ writeStore }) => {
  const activeStep = writeStore.activeStep;

  const renderInput = i => {
    switch (i) {
      case 0:
        return <ThemesList />;
      case 1:
        return <MessageInput />;
      default:
        return null;
    }
  };

  return (
    <>
      <h1>Dit is jou gekozen kaartje binnen het thema "culinair"!</h1>
      <Steps />
      <h2>{writeStore.getStepTitle(activeStep)}</h2>
      {renderInput(activeStep)}
      <Card />
      <BottomButtons />
    </>
  );
};

Answer.propTypes = {
  writeStore: PropTypes.observableObject.isRequired
};

export default inject(`writeStore`)(observer(Answer));
