import React from "react";
import { inject, observer } from "mobx-react";

import Chip from "@material-ui/core/Chip";
import UIStore from "../store/UIStore";

const ThemesList = ({ writeStore }) => {
  const handleClick = e => {
    writeStore.theme = e;
  };

  const themes = {
    art: {
      nl: "kunst",
      fr: "art"
    },
    culinair: {
      nl: "culinair",
      fr: "culinaire"
    },
    news: {
      nl: "actualiteit",
      fr: "actualite"
    },
    tech: {
      nl: "technologie",
      fr: "technologie"
    },
    nature: {
      nl: "natuur",
      fr: "nature"
    },
    tourism: {
      nl: "toerisme",
      fr: "tourisme"
    }
  };

  return (
    <>
      {themes.map(theme => (
        <ThemeButton>
          {UIStore.userLanguage === "nl" ? theme.nl : theme.fr}
        </ThemeButton>
      ))}
    </>
  );
};

const ThemeButton = {

}

export default inject(`writeStore`)(observer(ThemesList));
