import React from "react";
import { inject, observer, PropTypes } from "mobx-react";
import { withRouter } from "react-router-dom";
import { Link } from "react-router-dom";
import { ROUTES } from "../constants";

import styled from "styled-components";
import backgroundImage from "../assets/img/adminBg.jpg";
import QRCode from "qrcode.react";

const Answer = ({ openStore, uiStore }) => {
  console.log(openStore.selectedCard);

  return (
    <StyledPage>
      <ThankLine>
        {uiStore.userLanguage === "nl"
          ? "Bedankt om dit kaartje van een antwoord te voorzien"
          : "Merci d'avoir répondu à cette carte"}
        !
      </ThankLine>
      <Follow>
        {uiStore.userLanguage === "nl" ? (
          <>
            Blijf dit kaartje <span>volgen</span> via onderstaande{" "}
            <span>QR-code</span>
          </>
        ) : (
          <>
            Continuez à <span>suivre</span> cette carte en utilisant{" "}
            <span>le code QR</span> ci-dessous
          </>
        )}
      </Follow>
      <Others>en kijk wat anderen te zeggen hebben!</Others>

      <Codes>
        <QRCode value={`index.html/${openStore.selectedCard.uniqueId}`} />
        <UniqueId>{openStore.selectedCard}</UniqueId>
      </Codes>
    </StyledPage>
  );
};

const StyledPage = styled.section`
  width: 100vw;
  height: 100vh;
  background-image: url(${backgroundImage});
  background-size: cover;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  z-index: 0;
`;

const ThankLine = styled.p`
  color: #4eb5bb;
  font-size: 2.5rem;
  font-family: "Nunito";
  font-weight: 600;
`;

const Follow = styled.h2`
  color: #4eb5bb;
  font-size: 3.5rem;
  font-family: "Nunito";
  font-weight: bold;
  padding: 2.5rem 0 1rem 0;

  & span {
    color: #4da0a4;
  }
`;

const Others = styled.p`
  color: #4da0a4;
  font-size: 2.5rem;
  font-family: "Nunito";
  font-weight: 600;
`;

const Codes = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  padding: 5rem;
`;

const UniqueId = styled.p`
  color: #4da0a4;
  font-size: 2.5rem;
  font-family: "Nunito";
  font-weight: bold;
  padding-top: 2rem;
`;

Answer.propTypes = {
  openStore: PropTypes.observableObject.isRequired
};

export default inject(
  `openStore`,
  `writeStore`,
  `uiStore`
)(withRouter(observer(Answer)));
