import React from "react";
import { inject, observer, PropTypes } from "mobx-react";
import { Link } from "react-router-dom";

import styled from "styled-components";
import Button from "@material-ui/core/Button";
import { ROUTES } from "../constants";
import backgroundImage from "../assets/img/adminBg.jpg";
import languageBtn from "../assets/img/languageBtn.png";
import activeLanguageBtn from "../assets/img/activeLanguageBtn.png";
import languageDivider from "../assets/img/languageDivider.png";

const Admin = ({ uiStore }) => {
  return (
    <StyledPage>
      <div>
        <StyledTitle>
          Kies uw taal <br />{" "}
          <StyledTitleSpan>choisissez votre langue</StyledTitleSpan>
        </StyledTitle>
        <LanguageContainer>
          <DutchOption>
            <LanguageBtn onClick={() => uiStore.setAdminLanguage("nl")}>
              NL
            </LanguageBtn>
            <div>
              <LanguageTitle>Nederlands</LanguageTitle>
              <LanguageTxt>
                Deze taal stelt u in om de installatie op te starten en heeft
                geen verdere invloed op de installatie zelf.
              </LanguageTxt>
            </div>
          </DutchOption>
          <img src={languageDivider} width="70" alt="language divider" />
          <div>
            <LanguageBtn onClick={() => uiStore.setAdminLanguage("fr")}>
              FR
            </LanguageBtn>
            <div>
              <LanguageTitle>français</LanguageTitle>
              <LanguageTxt>
                Vous définissez cette langue pour l'installation pour démarrer
                et n'a pas plus influence sur l'installation elle-même.
              </LanguageTxt>
            </div>
          </div>
        </LanguageContainer>
        <br />

        {uiStore.adminLanguage !== "" ? (
          <Link to={ROUTES.setup}>
            <Button variant="contained">
              {uiStore.adminLanguage === "nl"
                ? content.nl.startBtn
                : content.fr.startBtn}
            </Button>
          </Link>
        ) : null}
      </div>
    </StyledPage>
  );
};

const content = {
  nl: {
    startBtn: "Installatie verder configureren"
  },
  fr: {
    startBtn: "Configurer davantage l'installation"
  }
};

const StyledPage = styled.section`
  width: 100vw;
  height: 100vh;
  background-image: url(${backgroundImage});
  background-size: cover;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const StyledTitle = styled.h1`
  font-size: 40px;
  font-weight: bold;
  color: #8089ce;
  text-align: center;
  margin-bottom: 5rem;
`;

const StyledTitleSpan = styled.span`
  color: #4e5587;
`;

const LanguageBtn = styled.button`
  border: none;
  background-image: url(${languageBtn});
  background-size: contain;
  background-repeat: no-repeat;
  background-color: transparent;
  width: 20rem;

  font-family: "Aracne";
  font-size: 13rem;
  color: white;

  &:focus {
    outline: none;
    background-image: url(${activeLanguageBtn});
  }
`;

const LanguageContainer = styled.section`
  display: flex;
  flex-direction: row;
`;

const LanguageTitle = styled.h2`
  text-transform: uppercase;
  font-weight: bold;
  color: #bababa;
  font-size: 2rem;
  padding: 2rem 0;
`;

const LanguageTxt = styled.p`
  color: #bababa;
  font-size: 1.6rem;
  max-width: 31rem;
  font-weight: 600;
  line-height: 2rem;
`;

const DutchOption = styled.div`
  display: flex;
  flex-direction: column;
  align-items: flex-end;

  && div {
    display: flex;
    flex-direction: column;
    align-items: flex-end;
  }

  ${LanguageTitle} {
    color: #bababa;
  }

  ${LanguageTxt} {
    text-align: right;
    color: #bababa;
  }
`;

Admin.propTypes = {
  uiStore: PropTypes.observableObject.isRequired
};

export default inject(`uiStore`)(observer(Admin));
