import React from "react";
import { inject, observer } from "mobx-react";

import { makeStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import Button from "@material-ui/core/Button";

import ReactCardFlip from "react-card-flip";

const useStyles = makeStyles(theme => ({
  front: {
    display: "flex",
    flexWrap: "wrap",
    "& > *": {
      margin: theme.spacing(1),
      width: theme.spacing(80),
      height: theme.spacing(40),
      padding: "20px",
      overflow: "hidden"
    }
  },
  back: {
    display: "flex",
    flexWrap: "wrap",
    "& > *": {
      margin: theme.spacing(1),
      width: theme.spacing(85),
      height: theme.spacing(45),
      overflow: "hidden"
    }
  }
}));

const Card = props => {
  const classes = useStyles();

  return (
    <>
      <ReactCardFlip isFlipped={props.isFlipped} flipDirection="vertical">
        <div className={classes.front}>
          <Paper elevation={3}>
            <h3>Uw kaartje</h3>
            {props.data.theme ? <p>{props.data.theme}</p> : null}
            {props.data.message ? <p>{props.data.message}</p> : null}
            {props.data.answers
              ? props.data.answers.map(answer => <li key={answer}>{answer}</li>)
              : null}
            {props.openStore ? <p>{props.openStore.message}</p> : null}
          </Paper>
        </div>
        <div className={classes.back}>
          <Paper elevation={3}>
            <img
              src="https://images.unsplash.com/photo-1514918956881-335d75e3c0c2?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=2100&q=80"
              alt=""
            />
          </Paper>
        </div>
      </ReactCardFlip>
      <Button onClick={props.writeStore.handleFlipCard}>flip</Button>
    </>
  );
};

export default inject(`writeStore`)(observer(Card));
