import React from "react";
import { inject, observer } from "mobx-react";

import UIStore from "../store/UIStore";
import styled from "styled-components";

const ThemesList = ({ writeStore, uiStore }) => {
  const handleClickTheme = e => {
    writeStore.theme = e;
  };

  const themes = [
    {
      art: {
        nl: "kunst",
        fr: "art"
      }
    },
    {
      culinair: {
        nl: "culinair",
        fr: "culinaire"
      }
    },
    {
      news: {
        nl: "actualiteit",
        fr: "actualite"
      }
    },
    {
      tech: {
        nl: "technologie",
        fr: "technologie"
      }
    },
    {
      nature: {
        nl: "natuur",
        fr: "nature"
      }
    },
    {
      tourism: {
        nl: "toerisme",
        fr: "tourisme"
      }
    }
  ];

  return (
    <>
      {themes.map(theme => (
        <ThemeButton
          img={UIStore.userLanguage === "nl" ? theme.nl : theme.fr}
          key={theme}
          onClick={() => handleClickTheme(theme)}
        >
          <p>{uiStore.userLanguage === "nl" ? theme.nl : theme.fr}</p>
        </ThemeButton>
      ))}
    </>
  );
};

const ThemeButton = styled.button`
  background-image: url(${props => props.img}.png);
  border: none;
  width: 15rem;
  height: 10rem;
`;

export default inject(`writeStore`, `uiStore`)(observer(ThemesList));
